def stake(vault, lp, amnt, account):
    lp.approve(vault, amnt, addr(account))
    vault.stake(amnt, addr(account))

def stakeAll(vault, lp, account):
    lp.approve(vault, lp.balanceOf(account), addr(account))
    vault.stake(addr(account))

def withdraw(vault, lp, amnt, account):
    vault.withdraw(amnt, addr(account))

def withdrawAll(vault, lp, account):
    vault.withdraw(addr(account))

def main():
    if network.show_active() == 'development' :
        admin=accounts[0]
        creator=accounts[1]
        consumer=accounts[2]

    if network.show_active() == 'bsc-test' or network.show_active() == 'rinkeby' :
        print("Current Network:"+ network.show_active())
        accounts.add(config['wallets']['admin'])
        accounts.add(config['wallets']['creator'])
        accounts.add(config['wallets']['consumer'])

        admin= accounts[0]
        creator= accounts[1]
        consumer= accounts[2]

        vault=LPVault.deploy(addr(admin))
        lp= loadLP('0xAE4C99935B1AA0e76900e86cD155BFA63aB77A2a')
        # Test net contract address

        stake(vault, lp, 1000, admin)
        stakeAll(vault, lp, creator)

        withdraw(vault, lp, 1000, admin)
        withdrawAll(vault, lp, creator)

    if network.show_active() == 'bsc-main':
        accounts.add(config['wallets']['admin'])
        accounts.add(config['wallets']['creator'])
        accounts.add(config['wallets']['consumer'])

        admin= accounts[0]
        creator= accounts[1]
        consumer= accounts[2]

        vault=LPVault.deploy(addr(admin))
        lp= loadLP('0xce53728d94255409Dfe6DD78247A678270d3D08b')

        # Test net contract address
    balance_alert(admin, "admin")
    balance_alert(creator, "creator")
    balance_alert(consumer, "consumer")